<template>  <div>    <h4>基础信息：</h4>    <div class="row g-3">      <div class="col-sm-6">        <label for="capital" class="form-label">基础资金：</label>        <input type="number" min="0" class="form-control" id="capital" placeholder="" value="" required="">      </div>      <div class="col-sm-6">        <label for="start_date" class="form-label">开始交易日期：</label>        <input type="date" :max="now" class="form-control" id="start_date" placeholder="" value="" required="">      </div>      <div class="col-12">        <label for="income" class="form-label">月收入：</label>        <input type="number" min="0" class="form-control" id="income" placeholder="请输入您的月收入" required="">      </div>    </div>    <!--  <div class="mb-3">-->    <!--    <label for="capital" class="form-label">基础资金：</label>-->    <!--    <input type="number" class="form-control" id="capital" aria-describedby="emailHelp">-->    <!--  </div>-->    <!--  <div class="col-md-3">-->    <!--    <div class="mb-3">-->    <!--      <label for="start_date" class="form-label">开始日期：</label>-->    <!--      <input type="date" class="form-control" id="start_date" aria-describedby="emailHelp">-->    <!--    </div>-->    <!--    <div class="mb-3">-->    <!--      <label for="income" class="form-label">月收入：</label>-->    <!--      <input type="number" class="form-control" id="income" aria-describedby="emailHelp">-->    <!--    </div>-->    <!--  </div>-->  </div></template><script>import {ref} from "vue";export default {  name: "Basic",  setup() {    function getNow() {      let d = new Date()      let year = d.getFullYear().toString(); //获取年      let month = d.getMonth().toString();//获取月      let date = d.getDate().toString(); //获取当日      if (month.length === 1) {        month = '0' + month      }      if (date.length === 1) {        date = '0' + date      }      return year+'-' + month+'-' + date    }    let now = ref(getNow())    return {      now    }  },  mounted() {  }}</script><style scoped></style>