<template>  <div>    <h4>基础信息：</h4>    <div class="row g-3">      <div class="col-sm-6">        <label for="capital" class="form-label">基础资金：</label>        <input type="number" name="basic_money" min="0" class="form-control" id="capital" placeholder=""               v-model="$store.state.trade.basic_money"               required="">      </div>      <div class="col-sm-6">        <label for="start_date" class="form-label">开始交易日期：</label>        <input type="date" name="start_date" :max="now" class="form-control" id="start_date" placeholder=""               v-model="$store.state.trade.start_date"               required="">      </div>      <div class="col-12">        <label for="income" class="form-label">月收入：</label>        <input type="number" name="month_income" min="0" class="form-control" id="income" placeholder="请输入您的月收入"               v-model="$store.state.trade.month_income" required="">      </div>    </div>  </div></template><script>import {ref} from "vue";import $store from "@/store"export default {  name: "Basic",  setup() {    let basic_money = ref('')    let start_date = ref('')    let month_income = ref('')    function getNow() {      let d = new Date()      let year = d.getFullYear().toString(); //获取年      let month = d.getMonth().toString();//获取月      let date = d.getDate().toString(); //获取当日      if (month.length === 1) {        month = '0' + month      }      if (date.length === 1) {        date = '0' + date      }      return year + '-' + month + '-' + date    }    let now = ref(getNow())    return {      now,      basic_money,      start_date,      month_income    }  },  mounted() {  }}</script><style scoped></style>